import { useHistory, useParams } from "react-router-dom";
import { useState } from "react";
import useFetch from "./useFetch";

const BlogDetails = () => {
    const { id } = useParams();
    const {data: blog, error, isPending} = useFetch('http://localhost:8000/blogs/' + id);
    const history = useHistory();

    const [like, setLike] = useState(0);
    //const [dislike, setDislike] = useState(0);
    const handleClickLike = () => { let count= like; count++; setLike(count); }
    //const handleClickDislike = () => { let count= dislike; count++; setDislike(count); }

    const handleClickUpdate= (e) =>{
        
        e.preventDefault();
        const blog = {title, body, author, likes};
    
        //console.log(blog);
        setIsPending(true);
        fetch('http://localhost:8000/blogs/', {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body : JSON.stringify(blog)
        }).then(() => {
            console.log('new blog added');
            setIsPending(false);
        });
    }

    const handleClickDelete= () => {
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: 'DELETE'
        }).then(() =>{
            history.push('/')
        })
    }

    return ( 
        <div className="blog-details">
            { isPending && <div>Loading...</div> }
            { error && <div>{ error }</div> }
            { blog && (
                <article>
                    <h2>{ blog.title }</h2>
                    <p>Publicado por { blog.author } </p>
                    <div>{ blog.body }</div>
                    

                    <button onClick={handleClickLike}>Me gusta</button>
                    <p>Me gusta: { like }</p>
                    <div className="blog-delete">
                        <button onClick={handleClickDelete}>Eliminar</button>
                    </div>
                </article>
            ) }
        </div>
     );
}
 
export default BlogDetails;
