import { useState } from "react";
import BlogList from "./BlogList";

const Home = () => {
    
    const [blogs, setBlogs] = useState([
        {title: 'Excursión 1', body: 'lorem ipsum...', author: 'juanjo', id: 1},
        {title: 'Excursión 2', body: 'lorem ipsum...', author: 'luis', id: 2},
        {title: 'Excursión 3', body: 'lorem ipsum...', author: 'juanjo', id: 3}
    ]);
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);

    const handleClickLike = () => {
        let count= like;
        count++;
        setLike(count);
    }
    const handleClickDislike = () => {
        let count= dislike;
        count++;
        setDislike(count);
    }

    

    return ( 
        <div className="home">
            <h2>HomePage</h2>
            <BlogList blogs={blogs} title="All Blogs!" />
            
            <p>Blog liked by { like }</p>
            <p>Blog disliked by { dislike }</p>

            <button onClick={handleClickLike}>Like</button>
            <button onClick={handleClickDislike}>Dislike</button>
        </div>
     );
}
 
export default Home;
