import { useState } from "react";
import { useHistory } from "react-router-dom";

const Create = () => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState('juanjo');
    const [isPending, setIsPending] = useState(false);
    const history = useHistory();

    const handleSubmit = (e) => {
        e.preventDefault();
        const blog = {title, body, author};

        //console.log(blog);
        setIsPending(true);
        fetch('http://localhost:8000/blogs/', {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body : JSON.stringify(blog)
        }).then(() => {
            console.log('new blog added');
            setIsPending(false);
        });

        history.go('/');
    }

    return ( 
        <div className="create">
            <h2>Añadir entrada</h2>
            <form onSubmit={handleSubmit}>
                <label>Título</label>
                <input type="text"
                required
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                />
                <label>Cuerpo</label>
                <textarea
                required
                value={body}
                onChange={(e) => setBody(e.target.value)}
                ></textarea>
                <label>Autor: </label>
                <select
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}

                >
                    <option value="juanjo">juanjo</option>
                    <option value="jose maria">jose maria</option>
                    <option value="anonimo">anonimo</option>

                </select>
                { !isPending && <button>Añadir</button> }
                { isPending && <button disabled>Añadiendo...</button> }

                <p>{ title }</p>
                <p>{ body }</p>
                <p>{ author }</p>

            </form>
        </div>
     );
}
 
export default Create;
