import BlogList from "./BlogList";
import useFetch from "./useFetch";
import Announcer from "./Announcer";
import { useState } from "react";
import { useHistory } from "react-router";


const posts = [
    { id: '1', name: 'This first post is about React' },
    { id: '2', name: 'This next post is about Preact' },
    { id: '3', name: 'We have yet another React post!' },
    { id: '4', name: 'This is the fourth and final post' },
];

const filterPosts = (posts, query) => {
    if (!query) {
        return posts;
    }
    return posts.filter((post) => {
        const postName = post.name.toLowerCase();
        return postName.includes(query);
    });
};


const SearchBlog = () => {
    const {data : blogs, isPending, error} = useFetch('http://localhost:8000/blogs');
    const { search } = window.location;
    const query = new URLSearchParams(search).get('s');
    const [searchQuery, setSearchQuery] = useState(query || '');

    const filterBlogs = (blogs, query) => {
        if (!query) {
            return blogs;
        }
        return blogs.filter((blog) => {
            const postTitle = blog.title.toLowerCase();
            console.log('titulo');
            console.log(postTitle);
            return postTitle.includes(query);
        });
    };
    const filteredBlogs = filterBlogs(blogs, query);
    

    return ( 
        <div className="search">
            <h2>Buscar blog</h2>
            <form action="/" method="get">
            <label htmlFor="header-search">
            </label>
            <input
                type="text"
                id="header-search"
                placeholder="Introduce tu consulta"
                name="s" 
            />
            <button type="submit">Buscar</button>
            </form>
            {blogs && <ul>
                {filteredBlogs.map(blog => (
                    <li key={blog.key}>{blog.title}</li>
                ))}
            </ul>}

            

        </div>

     );

}
    
 
export default SearchBlog;


