import { useHistory, useParams } from "react-router-dom";
import { useState } from "react";
import useFetch from "./useFetch";

const BlogDetails = () => {
    const { id } = useParams();
    const {data: blog, error, isPending} = useFetch('http://localhost:8000/blogs/' + id);
    const history = useHistory();

    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);
    const handleClickLike = () => { let count= like; count++; setLike(count); }
    const handleClickDislike = () => { let count= dislike; count++; setDislike(count); }

    const handleClickDelete= () => {
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: 'DELETE'
        }).then(() =>{
            history.push('/')
        })
    }

    return ( 
        <div className="blog-details">
            { isPending && <div>Loading...</div> }
            { error && <div>{ error }</div> }
            { blog && (
                <article>
                    <h2>{ blog.title }</h2>
                    <p>Publicado por { blog.author } </p>
                    <div>{ blog.body }</div>
                    

                    <button onClick={handleClickLike}>Me gusta</button>
                    <button onClick={handleClickDislike}>No me gusta</button>
                    <p>Me gusta: { like } / No me gusta: { dislike }</p>

                    <button onClick={handleClickDelete}>Eliminar</button>
                </article>
            ) }
        </div>
     );
}
 
export default BlogDetails;
