import { useHistory, useParams } from "react-router-dom";
import { useState } from "react";
import useFetch from "./useFetch";
import imoclin from "./images/imoclin.jpg";
import iestrella from "./images/iestrella.jpg";
import Images from "./Images";


const BlogDetails = () => {
    const { id } = useParams();
    const {data: blog, error, isPending} = useFetch('http://localhost:8000/blogs/' + id);
    const history = useHistory();
    const [image, setImage] = useState(imoclin);


    const handleClickUpdate= (e) =>{
        e.preventDefault();
    
        console.log(blog);
        let count= blog.likes; count++; blog.likes = count;
        
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: "PUT",
            headers: {"Content-Type": "application/json"},
            body : JSON.stringify(blog)
        }).then(() => {
            console.log('blog updated');
        });
        history.push(0)
    }
    const handleClickDelete= () => {
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: 'DELETE'
        }).then(() =>{
            history.push('/')
        })
    }    
    const setImagen = () => {
        console.log('imagen');
        console.log(blog.image);
        /* for i=0 i<4 i++
            if blog.image === Images[i].title
                _idImagen = i
        
        _idImagen = Images.find(blog.image)
*/
    }
        

    return ( 
        <div className="blog-details">
            { isPending && <div>Loading...</div> }
            { error && <div>{ error }</div> }
            { blog && setImagen }
            { blog && (
                <article>
                    <h2>{ blog.title }</h2>
                    <p>Publicado por { blog.author } </p>
                    

                    <img src={Images[(Images.find(x => x.title == blog.image).id)/(_idImagen)].src} className="App-logo" alt="image" />


                    <div>{ blog.body }</div>
                    <button onClick={handleClickUpdate}>Me gusta</button>
                    <p>Me gusta: { blog.likes }</p>
                    <div className="blog-delete">
                    <button onClick={handleClickDelete}>Eliminar</button>
                    </div>
                </article>
            ) }
        </div>
     );
}
 
export default BlogDetails;
 
