import { useHistory, useParams } from "react-router-dom";
import { useState } from "react";
import useFetch from "./useFetch";
import idefault from './idefault.jpg';
import iestrella from './iestrella.jpg';
import ijerez from './ijerez.jpg';
import imoclin from './imoclin.jpg';
import imulhacen from './imulhacen.jpg';


const BlogDetails = () => {
    const { id } = useParams();
    const {data: blog, error, isPending} = useFetch('http://localhost:8000/blogs/' + id);
    const history = useHistory();
    //const [like, setLike] = useState(0);


    const handleClickUpdate= (e) =>{
        e.preventDefault();
    
        console.log(blog);
        let count= blog.likes; count++; blog.likes = count;
        
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: "PUT",
            headers: {"Content-Type": "application/json"},
            body : JSON.stringify(blog)
        }).then(() => {
            console.log('blog updated');
        });
        history.push(0)
    }
    const handleClickDelete= () => {
        fetch('http://localhost:8000/blogs/' + blog.id, {
            method: 'DELETE'
        }).then(() =>{
            history.push('/')
        })
    }
    const setImage= () =>{
        image = blog.image;
    }

    return ( 
        <div className="blog-details">
            { isPending && <div>Loading...</div> }
            { !isPending && setImage }
            { error && <div>{ error }</div> }
            { blog && (
                <article>
                    <h2>{ blog.title }</h2>
                    <p>Publicado por { blog.author } </p>
                    <img src={blog.image} className="App-logo" alt="logo" />
                    <div>{ blog.body }</div>
                    <button onClick={handleClickUpdate}>Me gusta</button>
                    <p>Me gusta: { blog.likes }</p>
                    <div className="blog-delete">
                        <button onClick={handleClickDelete}>Eliminar</button>
                    </div>
                </article>
            ) }
        </div>
     );
}
 
export default BlogDetails;
