import { useEffect, useState } from "react";
import BlogList from "./BlogList";

const Home = () => {
    
    const [blogs, setBlogs] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);

    const handleClickLike = () => { let count= like; count++; setLike(count); }
    const handleClickDislike = () => { let count= dislike; count++; setDislike(count); }

    useEffect(() => {
        setTimeout(() => {
                fetch('http://localhost:8000/blogs')
                .then(res => {
                    console.log(res);
                    if (!res.ok){
                        throw Error('Could not fetch the data for that resource.\nContact the administrator.');
                    }
                    return res.json();
                })
                .then(data => {
                    console.log(data);
                    setBlogs(data);
                    setIsPending(false);
                    setError(null);
                })
                .catch(err => {
                    console.log(err.message);
                    setIsPending(false);
                    setError(err.message);
                })
        }, 500);
    }, []);
    

    return ( 
        <div className="home">
            {error && <div>{ error }</div> }
            {isPending && <div>Loading...</div>}
            {blogs && <BlogList blogs={blogs} title="All Blogs!"/>}
            {(!isPending && setError) && <button onClick={handleClickLike}>Like</button>}
            {!isPending && <button onClick={handleClickDislike}>Dislike</button>}
            {!isPending && <p>Liked { like } / Disliked { dislike }</p>}
        </div>
     );
}
 
export default Home;
